                    text = "–í 2011 –≥–æ–¥—É –±—ã–ª –∏–∑–¥–∞–Ω –Ω–æ–≤—ã–π —Å—Ç–∞–Ω–¥–∞—Ä—Ç C++11, –∫–æ—Ç–æ—Ä—ã–π —Å–æ–¥–µ—Ä–∂–∞–ª –º–Ω–æ–∂–µ—Å—Ç–≤–æ –¥–æ–±–∞–≤–ª–µ–Ω–∏–π –∏ –æ–±–æ–≥–∞—â–∞–ª —è–∑—ã–∫ –°++ –±–æ–ª—å—à–∏–º —á–∏—Å–ª–æ–º –Ω–æ–≤—ã—Ö —Ñ—É–Ω–∫—Ü–∏–æ–Ω–∞–ª—å–Ω—ã—Ö –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–µ–π. –° —Ç–µ—Ö –ø–æ—Ä –±—ã–ª–æ –≤—ã–ø—É—â–µ–Ω–æ –µ—â–µ —Ä—è–¥ —Å—Ç–∞–Ω–¥–∞—Ä—Ç–æ–≤. –ù–∞ –º–æ–º–µ–Ω—Ç –Ω–∞–ø–∏—Å–∞–Ω–∏—è –¥–∞–Ω–Ω–æ–≥–æ —É—Ä–æ–∫–∞ —Å–∞–º—ã–π –ø–æ—Å–ª–µ–¥–Ω–∏–π —Å—Ç–∞–Ω–¥–∞—Ä—Ç - C++20 –±—ã–ª –æ–ø—É–±–ª–∏–∫–æ–≤–∞–Ω –≤ –¥–µ–∫–∞–±—Ä–µ 2020 –≥–æ–¥–∞. –í –∫–æ–Ω—Ü–µ 2023 –≥–æ–¥–∞ –æ–∂–∏–¥–∞–µ—Ç—Å—è –≤—ã—Ö–æ–¥ —Å—Ç–∞–Ω–¥–∞—Ä—Ç–∞ C++23",
                    style = MaterialTheme.typography.bodySmall
                )
                Spacer(modifier = Modifier.size(8.dp))
            }
        }
    }
}
@Composable
fun Lesson3Practice(
    viewModel: LessonScreenViewModel,
    navController: NavController,
) {
    Column(
        modifier = Modifier
            .fillMaxSize()
            .padding(8.dp),
        horizontalAlignment = Alignment.CenterHorizontally,
        verticalArrangement = Arrangement.Center
    ) {
        Text(text = "–î–ª—è —ç—Ç–æ–π —Ç–µ–º—ã –Ω–µ—Ç –∑–∞–¥–∞–Ω–∏–π, –≤—ã –º–æ–∂–µ—Ç–µ –ø—Ä–æ–π—Ç–∏ –¥–∞–ª—å—à–µ üòâ", textAlign = TextAlign.Center)
        Button(onClick = {
            viewModel.update(LessonDestination.idlesson+1, LessonDestination.idlesson)
            viewModel.progress += 1f
            if(viewModel.progress == 1f) {
                navController.navigate(FinishDestination.route)
            }
        }) {
            Text(text = "–ó–∞–∫–æ–Ω—á–∏—Ç—å —É—Ä–æ–∫")
        }
    }
